const signupFormHandler=async function(a){a.preventDefault();const b=document.querySelector("#username-input-signup"),c=document.querySelector("#email-input-signup"),d=document.querySelector("#password-input-signup"),e=await fetch("/api/users",{method:"POST",body:JSON.stringify({username:b.value,email:c.value,password:d.value}),headers:{"Content-Type":"application/json"}});e.ok?document.location.replace("/dashboard"):alert("Failed to sign up")};document.querySelector("#signup-form").addEventListener("submit",signupFormHandler);