const loginFormHandler=async a=>{a.preventDefault();const b=document.querySelector("#username-login").value.trim(),c=document.querySelector("#password-login").value.trim();if(b&&c){const a=await fetch("/api/users/login",{method:"POST",body:JSON.stringify({username:b,password:c}),headers:{"Content-Type":"application/json"}});a.ok?document.location.replace("/dashboard"):alert(a.statusText)}};document.querySelector("#login-form").addEventListener("submit",loginFormHandler);